    var base = "http://dev.ruishangquan.com/"
    var winH = $(window).height();
    var args = {};
    var supportTouch = function(){
        try {
            document.createEvent("TouchEvent");
            return true;
        } catch (e) {
            return false;
        }
    }();
    var pages = {};
    var pageManager = {
        $container: $('#container'),
        _pageStack: [],
        _configs: [],
        _pageAppend: function(){},
        _defaultPage: null,
        _pageIndex: 1,
        setDefault: function (defaultPage) {
            this._defaultPage = this._find('name', defaultPage);
            return this;
        },
        setPageAppend: function (pageAppend) {
            this._pageAppend = pageAppend;
            return this;
        },
        init: function () {
            var self = this;

            $(window).on('hashchange', function () {
                var state = history.state || {};
                var url = location.hash.indexOf('#') === 0 ? location.hash : '#';
                var page = self._find('url', url) || self._defaultPage;
                if (state._pageIndex <= self._pageIndex || self._findInStack(url)) {
                    self._back(page);
                } else {
                    self._go(page);
                }
            });

            if (history.state && history.state._pageIndex) {
                this._pageIndex = history.state._pageIndex;
            }

            this._pageIndex--;

            var url = location.hash.indexOf('#') === 0 ? location.hash : '#';
            var page = self._find('url', url) || self._defaultPage;
            this._go(page);
            return this;
        },
        push: function (config) {
            this._configs.push(config);
            return this;
        },
        go: function (to) {
            var config = this._find('name', to);
            if (!config) {
                return;
            }
            location.hash = config.url;
        },
        _go: function (config) {
            this._pageIndex ++;

            history.replaceState && history.replaceState({_pageIndex: this._pageIndex}, '', location.href);

            var html = $(config.template).html();
            var $html = $(html).addClass('slideIn').addClass(config.name);
            $html.on('animationend webkitAnimationEnd', function(){
                $html.removeClass('slideIn').addClass('js_show');
            });
            this.$container.append($html);
            this._pageAppend.call(this, $html);
            this._pageStack.push({
                config: config,
                dom: $html
            });

            if (!config.isBind) {
                this._bind(config);
            }

            return this;
        },
        back: function () {
            history.back();
        },
        _back: function (config) {
            this._pageIndex --;

            var stack = this._pageStack.pop();
            if (!stack) {
                return;
            }

            var url = location.hash.indexOf('#') === 0 ? location.hash : '#';
            var found = this._findInStack(url);
            if (!found) {
                var html = $(config.template).html();
                var $html = $(html).css('opacity', 1).addClass(config.name);
                $html.insertBefore(stack.dom);

                if (!config.isBind) {
                    this._bind(config);
                }

                this._pageStack.push({
                    config: config,
                    dom: $html
                });
            }

            stack.dom.addClass('slideOut').on('animationend', function () {
                stack.dom.remove();
            }).on('webkitAnimationEnd', function () {
                stack.dom.remove();
            });

            return this;
        },
        _findInStack: function (url) {
            var found = null;
            for(var i = 0, len = this._pageStack.length; i < len; i++){
                var stack = this._pageStack[i];
                if (stack.config.url === url) {
                    found = stack;
                    break;
                }
            }
            return found;
        },
        _find: function (key, value) {
            var page = null;
            for (var i = 0, len = this._configs.length; i < len; i++) {
                if (this._configs[i][key] === value) {
                    page = this._configs[i];
                    break;
                }
            }
            return page;
        },
        _bind: function (page) {
            var events = page.events || {};
            for (var t in events) {
                for (var type in events[t]) {
                    var that = this;
                    if(type == 'click' && supportTouch){
                        (function(dom, event){
                            var touchStartY;
                            that.$container.on('touchstart', dom, function (e) {
                                touchStartY = e.changedTouches[0].clientY;
                            });
                            that.$container.on('touchend', dom, function (e) {
                                if (Math.abs(e.changedTouches[0].clientY - touchStartY) > 10) return;
                                e.preventDefault();

                                events[dom][event].call(this, e);
                            });
                        })(t, type);
                    }else{
                        this.$container.on(type, t, events[t][type]);
                    }
                }
            }
            page.isBind = true;
        },

        GetQueryString:function (name)
        {
            var reg = new RegExp("(^|&)"+ name +"=([^&]*)(&|$)");
            var r = window.location.search.substr(1).match(reg);
            if(r!=null)return  unescape(r[2]); return null;
        },

        show:function (def) {
            var tpls = $('script[type="text/html"]');
            window.home = function(){
                location.hash = '';
            };

            for (var i = 0, len = tpls.length; i < len; ++i) {
                var tpl = tpls[i], name = tpl.id.replace(/tpl_/, '');

                pages[name] = {
                    name: name,
                    url: '#' + name,
                    template: '#' + tpl.id
                };
            }

            for (var page in pages) {
                pageManager.push(pages[page]);

            }

            var home = "index";

            if (def!="") {
                home = def;
            }

            pageManager
                .setPageAppend(function($html){
                    var $foot = $html.find('.page__ft');
                    if($foot.length < 1) return;

                    if($foot.position().top + $foot.height() < winH){
                        $foot.addClass('j_bottom');
                    }else{
                        $foot.removeClass('j_bottom');
                    }
                })
                .setDefault(home)
                .init();

            Handlebars.registerHelper('ifcond', function(v1, v2, options) {
                if(v1 > v2) {
                    return options.fn(this);
                }
                return options.inverse(this);
            });



             Handlebars.registerHelper('compare', function(left, operator, right, options) {
                if (arguments.length < 3) {
                    throw new Error('Handlerbars Helper "compare" needs 2 parameters');
                }
                var operators = {
                    '==':     function(l, r) {return l == r; },
                    '===':    function(l, r) {return l === r; },
                    '!=':     function(l, r) {return l != r; },
                    '!==':    function(l, r) {return l !== r; },
                    '<':      function(l, r) {return l < r; },
                    '>':      function(l, r) {return l > r; },
                    '<=':     function(l, r) {return l <= r; },
                    '>=':     function(l, r) {return l >= r; },
                    'typeof': function(l, r) {return typeof l == r; }
                };


                if (!operators[operator]) {
                    throw new Error('Handlerbars Helper "compare" doesn\'t know the operator ' + operator);
                }

                var result = operators[operator](left, right);

                if (result) {
                    return options.fn(this);
                } else {
                    return options.inverse(this);
                }
            });







            // 初始化控制
            accordion.bind();
            searchbar.init();

            // .container 设置了 overflow 属性, 导致 Android 手机下输入框获取焦点时, 输入法挡住输入框的 bug
            // 相关 issue: https://github.com/weui/weui/issues/15
            // 解决方法:
            // 0. .container 去掉 overflow 属性, 但此 demo 下会引发别的问题
            // 1. 参考 http://stackoverflow.com/questions/23757345/android-does-not-correctly-scroll-on-input-focus-if-not-body-element
            //    Android 手机下, input 或 textarea 元素聚焦时, 主动滚一把
            if (/Android/gi.test(navigator.userAgent)) {
                window.addEventListener('resize', function () {
                    if (document.activeElement.tagName == 'INPUT' || document.activeElement.tagName == 'TEXTAREA') {
                        window.setTimeout(function () {
                            document.activeElement.scrollIntoViewIfNeeded();
                        }, 0);
                    }
                })
            }
        }

    };

    /* ==========================================================================
     封装  ajax数据请求
     { options:url,  必选
     options:data,  必选
     success:function(data){},  必选
     error:function(message){}  可选
     }
     ============================================================================ */

    var ajax={

        //标准 ajax get 方法
        get:function (options) {
            toast.show("加载中");
            return $.ajax({
                type: 'GET',
                url: options.url,
                data: options.data,
                dataType: 'json',
                context: $('body'),
                crossDomain: true,
                xhrFields: {
                    withCredentials: true
                },
                success: function (data) {
                    if (data.message.type == "success") {
                        toast.close();
                        options.success(data.data);
                    } else {
                        if (options.error != null) {
                            options.error(data.message);
                        } else {
                            toast.show(data.message.content);
                        }
                    }
                },
                error: function (xhr, type) {
                    if (options.error != null) {
                        options.error(data.message);
                    } else {
                        toast.show("获取数据失败");
                    }
                }
            })
        },

        //标准 ajax post 方法
        post:function (options) {
            toast.show("加载中");
            $.ajax({
                type: 'POST',
                url: options.url,
                data: options.data,
                dataType: 'json',
                context: $('body'),
                crossDomain: true,
                xhrFields: {
                    withCredentials: true
                },
                contentType:(options.contentType!==""&&options.contentType!==undefined)?options.contentType:"application/x-www-form-urlencoded",
                processData:(options.processData!==""&&options.processData!==undefined)?options.processData:true,
                success: function (data) {
                    if (data.message.type == "success") {
                        toast.close();
                        options.success(data.data);
                    } else {
                        if (options.error != null) {
                            options.error(data.message);
                        } else {
                            toast.show(data.message.content);
                        }
                    }
                },
                error: function (xhr, type) {
                    if (options.error != null) {
                        options.error(data.message);
                    } else {
                        toast.show("获取数据失败");
                    }
                }
            })
        }

    }

    var render={
        fill:function(el,data) {
            var tpl = Handlebars.compile(el.html());
            return tpl(data);
        }

    }






    
