function getAreas(phonetic) {
	var areas = [];
	for(var i=0;i<citys.length;i++)
	{
		if (citys[i].phonetic==phonetic) {
			areas.push(citys[i]);
		}
	}
	return areas;
}


function getTagAreas(status) {
	var areas = [];
	for(var i=0;i<citys.length;i++)
	{
		if (citys[i].status==status) {
			areas.push(citys[i]);
		}
	}
	return areas;
}


function getHotCity() {
	var areas = [];
	for (var i = 0; i < citys.length; i++) {
		if (citys[i].tag == 1) {
			areas.push(citys[i]);
		}
	}
}


